const initialState = {
      filters: []
}

const filters = (state = initialState, action) => {
      switch (action.type) {

            case 'HEROES_FILTER':
                  return {
                        ...state,
                        filterHeroes: action.payload,
                  }
            default: return state
      }
}
export default filters;
