import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const fetchForm = createAsyncThunk(
      "formRegisterSlice/fetchForm",
      async (registerObj) => {
            fetch("http://localhost:3001/users", {
                  method: 'POST',
                  headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json',
                  },
                  body: JSON.stringify(state.registerObj)
            })
      }
)

const initialState = {
      registerObj: [],
}


const formRegisterSlice = createSlice({
      name: "formRegisterSlice",
      initialState,
      reducers: {
            onRegistrSubmitForm: (state, action) => {
                  state.registerObj.push(action.payload)
            },
      },
      // extraReducers: (builder) => {
      //       builder
      //             .addCase(fetchForm.fulfilled, (state, action) => {
      //                   state.registerObj = action.payload
      //             })
      // }
})


const { reducer, actions } = formRegisterSlice;

export default reducer;
export const { onRegistrSubmitForm } = actions;
