import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const fetchChar = createAsyncThunk(
      'aboutSlice/fetchChar',

      async function (url, rejectWithValue) {
            try {
                  const response = await fetch("https://gateway.marvel.com:443/v1/public/characters?apikey=4ca4e0f7a1c0e3bdc1240a5027d68f5f")
                  if (!response.ok) {
                        throw new Error(`Could not fetch ${url}, status: ${response.status}`);
                  }
                  const data = await response.json()
                        .then(res => {
                              res.data.results.map(_transformCharacter)
                        })

                  const _transformCharacter = (char) => {
                        return {
                              id: char.id,
                              name: char.name,
                              description: char.description,
                              // thumbnail: char.thumbnail.path + "." + char.thumbnail.extension,
                              // homepage: char.urls[0].url,
                              // wiki: char.urls[1].url,
                              // comics: char.comics.items,
                        };
                  };

                  return data;

            } catch (error) {
                  throw (rejectWithValue(error.massage))
            }
      }
)


const initialState = {
      char: [],
      status: '',
      error: null,
}

const aboutSlice = createSlice({
      name: 'aboutSlice',
      initialState,
      reducers: {},
      extraReducers: (builder) => {
            builder
                  .addCase(fetchChar.pending, (state) => {
                        state.status = "Loading"
                  })
                  .addCase(fetchChar.fulfilled, (state, action) => {
                        state.status = "fulfilled";
                        state.char = action.payload
                  })
                  .addCase(fetchChar.rejected, (state, action) => {
                        state.status = 'Error';
                        state.error = action.payload;
                  })
      }
})

const { actions, reducer } = aboutSlice;
export default reducer;
export const { } = actions;
