import './scssPages/about.scss'
import { useEffect, useRef, useMemo } from 'react';
//import { useHttp } from '../../hooks/http.hook';
import Spinner from '../spinner/Spinner';
import { useDispatch, useSelector } from 'react-redux';
import { fetchChars, fetchChar } from './slicePages/aboutSlice';

const About = () => {

      const dispatch = useDispatch();
      const chars = useSelector(state => state.aboutSlice.chars)
      const char = useSelector(state => state.aboutSlice.char)
      //const idDescr = useSelector(state => state.aboutSlice.idDescr)
      const statusChar = useSelector(state => state.aboutSlice.statusChar)
      // const urlOneChsr = `https://gateway.marvel.com:443/v1/public/characters/${idDescr}?apikey=4ca4e0f7a1c0e3bdc1240a5027d68f5f`
      const urlAllChars = "https://gateway.marvel.com:443/v1/public/characters?limit=40&apikey=4ca4e0f7a1c0e3bdc1240a5027d68f5f"


      useEffect(() => {
            dispatch(fetchChars(urlAllChars))

      }, [])

      const serviceChars = useMemo(() => chars.map(char => {
            console.log(1);
            return {
                  id: char.id,
                  name: char.name,
                  description: char.description,
                  thumbnail: char.thumbnail.path + "." + char.thumbnail.extension,
                  homepage: char.urls[0].url,
                  wiki: char.urls[1].url,
                  comics: char.comics.items,
            };
      }), [chars])

      const resChar = serviceChars(chars)


      if (resChar.length < 1) {
            return (
                  <div className='spinner'>
                        <Spinner />
                  </div>
            )
      };

      //const activeClass = document.querySelector(".about__description");

      return (
            <div className='about__wrapper'>
                  <ul className='about'>
                        {<ComponentChar resChar={resChar} />}
                  </ul>
                  <div className='about__description'>
                        <div className='about__description-inner'>
                              {/* {statusChar === "Error" ? noImges : null} */}
                              {statusChar === "" ? <div>Кликните по карточке с героем.</div> : null}
                              {statusChar === "Loading" ? <Spinner /> : <img className='about__description-imges' src={char} alt="" />}
                        </div>
                  </div>
            </div>

      );
}

const ComponentChar = (props) => {

      const dispatch = useDispatch()
      const { resChar } = props;
      const refer = useRef([]);

      const focuseItem = (i) => {

            refer.current.forEach((item) => {
                  item.classList.remove('activer')
            });
            refer.current[i].classList.add('activer');
            const activeClass = document.querySelector(".about__description");
            activeClass.classList.add('activer')
      }

      return (
            <div>
                  {
                        resChar.map((item, i) => {
                              return (
                                    <li ref={(el) => (refer.current[i] = el)}
                                          onClick={(e) => {
                                                focuseItem(i, e);
                                                //dispatch(getIdDescription(e.target.id));
                                                dispatch(fetchChar(`https://gateway.marvel.com:443/v1/public/characters/${e.target.id}?apikey=4ca4e0f7a1c0e3bdc1240a5027d68f5f`));
                                          }
                                          }
                                          key={i}
                                          id={item.id}
                                          className='about__list' >
                                          <div className='about__list-name'>{item.name}</div>
                                          <div className='about__list-descr'>{item.description}</div>
                                          <div className='about__list-id'>{item.id}</div>
                                    </li>
                              )
                        })
                  }
            </div>

      )


}



export default About;
