import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const fetchForm = createAsyncThunk(
      "formRegisterSlice/fetchForm",
      async ({ regObj, dispatch }, rejectWithValue) => {
            dispatch(onRegistrSubmitForm(regObj))
            console.log(regObj);
            try {
                  const response = fetch("http://localhost:3001/users", {
                        method: 'POST',
                        headers: {
                              'Accept': 'application/json',
                              'Content-Type': 'application/json',
                        },
                        body: JSON.stringify(regObj)
                  })
                  if (!response.ok) {
                        throw new Error(`Could not fetch http://localhost:3001/users, status: ${response.status}`);
                  }
            } catch (error) {
                  rejectWithValue(error.massage)
            }

      }
)

const initialState = {
      stateSending: "",
      registerObj: [],
}


const formRegisterSlice = createSlice({
      name: "formRegisterSlice",
      initialState,
      reducers: {
            onRegistrSubmitForm: (state, action) => {
                  state.registerObj.push(action.payload)
            },
      },
      extraReducers: (builder) => {
            builder
                  .addCase(fetchForm.fulfilled, (state, action) => {
                        console.log(action.payload);
                        state.stateSending = 'fulfilled'

                        state.registerObj.push(action.payload)
                  })
      }
})


const { reducer, actions } = formRegisterSlice;

export default reducer;
export const { onRegistrSubmitForm } = actions;
