import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const sendingfetchForm = createAsyncThunk(
      "formRegisterSlice/fetchForm",
      async (regObj) => {

            try {
                  const response = fetch("http://localhost:3001/users", {
                        method: 'POST',
                        headers: {
                              'Accept': 'application/json',
                              'Content-Type': 'application/json',
                        },
                        body: JSON.stringify(regObj)
                  })
                  if (!response.ok) {
                        throw new Error(`Could not fetch http://localhost:3001/users, status: ${response.status}`);
                  }
            } catch (error) {

            }

      }
)

const initialState = {
      stateCheckbox: "",
      stateSending: "",
      registerObj: [],
}

const formRegisterSlice = createSlice({
      name: "formRegisterSlice",
      initialState,
      reducers: {
            onRegistrSubmitForm: (state, action) => {
                  state.registerObj.push(action.payload)
            },
            onCheckedForm: (state, action) => {
                  state.stateCheckbox = action.payload
            },
      },
      extraReducers: (builder) => {
            builder
                  .addCase(sendingfetchForm.fulfilled, (state) => {
                        state.stateSending = 'fulfilled'


                  })
      }
})


const { reducer, actions } = formRegisterSlice;

export default reducer;
export const { onRegistrSubmitForm, onCheckedForm } = actions;
