import { useState, useRef } from 'react';
import './ButtonLevel.scss'
import { current } from '@reduxjs/toolkit';


const ButtonLevel = (props) => {
      const { children, id, classes } = props
      const [styles, setStyles] = useState("level")




      const refButton = useRef()
      console.log(refButton);

      const focusLevel = (e) => {
            refButton.current.map((item) => {
                  item.remove.classList('level_active')
            })

            // item.add.classList('level_active')
      }
      //focusLevel()

      const callingElevator = () => {

            if (id) {
                  setStyles("level level_active")
            }

      }

      return (
            <div>
                  <button onClick={(e) => {
                        callingElevator(e)
                        focusLevel(e)
                  }}
                        id={id}
                        className={styles}
                        ref={refButton}>{children}</button>
            </div>
      );
}

export default ButtonLevel;
