import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const fetchForm = createAsyncThunk(
      'formRegisterSlice/fetchForm',
      async function (url, rejectWithValue) {
            try {
                  const response = await fetch(url)

                  if (!response.ok) {
                        throw new Error(`Could not fetch ${url}, status: ${response.status}`);
                  }
                  const data = await response.json()
                        .then(res => res.data.results[0].thumbnail.path + "." + res.data.results[0].thumbnail.extension)

                  return data;
            } catch (error) {
                  throw (rejectWithValue(error.massage))
            }
      }
)

const initialState = {
      registerObj: [],
}


const formRegisterSlice = createSlice({
      name: "formRegisterSlice",
      initialState,
      reducers: {
            onRegistrSubmitForm: (state, action) => {
                  state.registerObj.push(action.payload)
            },
      },
})


const { reducer, actions } = formRegisterSlice;

export default reducer;
export const { onRegistrSubmitForm } = actions;
