import React from 'react';
import { useEffect, useState } from 'react'
import './scssPages/hi.scss'
import { useSelector, useDispatch } from 'react-redux';
import Spinner from '../spinner/Spinner';
import { deleteShoppingCart } from './slicePages/aboutSlice';



const Cart = (props) => {

	const dispatch = useDispatch()
	const { v4: uuidv4 } = require('uuid');
	const shoppingCart = useSelector(state => state.aboutSlice.shoppingCart)
	const [stat, setStat] = useState([])
	const [check, setCheck] = useState({})
	const [count, setCount] = useState([
		{
			id: "",
			counter: 1,
		}
	])


	const deleteItem = (e) => {
		dispatch(deleteShoppingCart(shoppingCart.filter((item) => +item.id !== +e.target.id)))
	}

	// const incr = (e) => {
	//   setCount([
	//     ...count,

	//     {
	//       id: +e.target.id,
	//       counter: count.fine((element) => {
	//         if (element.id == e.target.id)
	//           //console.log(element.counter);
	//           return element.counter + 1
	//       })
	//     },

	//   ])
	//   // counter(count, e)
	// }




	const { id, thumbnail, description, name } = props;
	return (
		<>

			<li key={id} className='hi__shoppingcart-list'>
				<div className="hi__shoppingcart-wrapper">
					<div className='hi__shoppingcart-list-name'>{name}</div>
					<div className='hi__shoppingcart-list-descr'>{description}</div>
					<img src={thumbnail} className='hi__shoppingcart-list-img'></img>
					<div onClick={(e) => deleteItem(e)} id={id} className='hi__shoppingcart-delete'>❌
					</div>
				</div>
				<div className="hi__shoppingcart-select">
					<div className="hi__shoppingcart-selector">
						<div onClick={(e) => incr(e)} id={id} className="hi__shoppingcart-selector-incr">+</div>
						<div id={id} className="hi__shoppingcart-selector-summ">{count.counter}</div>
						<div id={id} className="hi__shoppingcart-selector-decr">-</div>
					</div>
					<input
						type='checkbox'
						className='hi__shoppingcart-checkbox'
						id={uuidv4()}
						onChange={() => console.log('g')}
						//checked={stat}
						//onClick={(e) => setStat(e)}
						name="terms" />
				</div>

			</li>
		</>
	);
}

export default Cart;
