import { useState } from 'react';
import './scssPages/formRegistr.scss'
import { useDispatch, useSelector } from 'react-redux';
import { onRegistrSubmitForm, sendingfetchForm } from './slicePages/formRegisterSlice';

const FormRegisr = () => {
      const dispatch = useDispatch();
      const [formValueLogin, setFormValueLogin] = useState('');
      const [formValuePassword, setFormValuePassword] = useState('');
      const [checked, setChecked] = useState(false)
      const { registerObj } = useSelector(state => state.formRegisterSlice)
      //const { stateCheckbox } = useSelector(state => state.formRegisterSlice)
      //const { v4: uuidv4 } = require('uuid');


      const toggleChecked = () => {
            setChecked(!checked)

      }


      console.log(checked);
      const onSubmit = (e) => {
            e.preventDefault();
            const resObj = {
                  login: formValueLogin,
                  password: formValuePassword,
            };
            dispatch(onRegistrSubmitForm(resObj))
            dispatch(sendingfetchForm(resObj))

            setFormValueLogin('');
            setFormValuePassword('');
            setChecked(false);
            alert(`Статус отправки:
                                    login: ${resObj.login},
                                    password: ${resObj.password}`)

      }


      return (
            <div className='form_registr'>
                  <h1>Форма регистрации</h1>
                  <form onSubmit={onSubmit} className='forma__registration'>
                        <label htmlFor="login">Логин: </label>
                        <input
                              className="form-control"
                              type="text"
                              required
                              onChange={(e) => setFormValueLogin(e.target.value)}
                              name="login"
                              value={formValueLogin}
                              id="login"
                              placeholder="Введите логин" />
                        <label htmlFor="password" className='forma__registration'> Пароль:</label>
                        <input
                              type="password"
                              className="form-control"
                              required
                              onChange={(e) => setFormValuePassword(e.target.value)}
                              name="password"
                              value={formValuePassword}
                              id="password"
                              placeholder="Введите пароль"
                        />


                        <label htmlFor='happy' className={checked ? 'custom-checkbox custom-checkbox-active' : 'custom-checkbox'}>
                              <input id="happy"
                                    type="checkbox"
                                    onChange={toggleChecked}
                                    className='custom-checkbox'
                                    checked={checked}
                              />Согласие на регистрацию
                        </label>
                        <button
                              disabled={!checked || !formValuePassword || !formValueLogin} type="submit" className="btn btn-primary forma__registration">отправить</button>
                  </form>

            </div>
      );
}

export default FormRegisr;
