import { useEffect } from 'react';
import './footer.scss'

import { useHttp } from '../../hooks/http.hook';


const Footer = () => {
      const { request } = useHttp()
      useEffect(() => {

            res()
            console.log(res);
      }, [])


      const res = () => {
            request("https://gateway.marvel.com:443/v1/public/characters?apikey=4ca4e0f7a1c0e3bdc1240a5027d68f5f")
            return res.data.results.map(_transformCharacter)
      };

      const _transformCharacter = (char) => {
            return {
                  id: char.id,
                  name: char.name,
                  description: char.description,
                  thumbnail: char.thumbnail.path + "." + char.thumbnail.extension,
                  homepage: char.urls[0].url,
                  wiki: char.urls[1].url,
                  comics: char.comics.items,
            };
      };











      return (

            <footer className='footer'>
                  <div className='container'>
                        <ul className='footer_nav'>
                              <li className='footer_nav-list'>WatsApp</li>
                              <li className='footer_nav-list'>Instagram</li>
                              <li className='footer_nav-list'>Telegram</li>
                              <li className='footer_nav-list'>About</li>
                        </ul>
                  </div>

            </footer>


      );
}

export default Footer;
