import './scssPages/about.scss'
import { useEffect, useRef } from 'react';
import { useHttp } from '../../hooks/http.hook';
import Spinner from '../spinner/Spinner';
import { useDispatch, useSelector } from 'react-redux';
import { fetchChar } from './slicePages/aboutSlice';




const About = () => {

      const dispatch = useDispatch()
      const chars = useSelector(state => state.aboutSlice)


      useEffect(() => {
            dispatch(fetchChar())

      }, [])

      const res = (chars) => chars.chars.map(char => {
            return {
                  id: char.id,
                  name: char.name,
                  description: char.description,
                  thumbnail: char.thumbnail.path + "." + char.thumbnail.extension,
                  homepage: char.urls[0].url,
                  wiki: char.urls[1].url,
                  comics: char.comics.items,
            };
      })

      const resChar = res(chars)

      const getDescription = (id) => {
            resChar.map((item) => {
                  return item.id == id
            })
      }


      if (resChar.length < 1) {
            return (
                  <div className='spinner'>
                        <Spinner />
                  </div>

            )
      };

      return (
            <div className='about__wrapper'>
                  <ul className='about'>
                        {<ComponentChar resChar={resChar} getDescription={getDescription} />}
                  </ul>
                  <div className='about__description'>
                        { }
                  </div>
            </div>

      );
}



const ComponentChar = (props) => {
      const { resChar, getDescription } = props;

      const refer = useRef([]);
      console.log(refer.current);

      const focuseItem = (i) => {
