import './scssPages/about.scss'
import { useEffect, useState } from 'react';
import { useHttp } from '../../hooks/http.hook';



const About = () => {
      const { request } = useHttp()
      const [chars, setChar] = useState([])

      console.log(chars);

      useEffect(() => {

            setChar(res())

      }, [])

      const res = async () => {
            const results = await request("https://gateway.marvel.com:443/v1/public/characters?apikey=4ca4e0f7a1c0e3bdc1240a5027d68f5f")
            return results.data.results.map(_transformCharacter)
      };
      const _transformCharacter = (char) => {
            return {
                  id: char.id,
                  name: char.name,
                  description: char.description,
                  thumbnail: char.thumbnail.path + "." + char.thumbnail.extension,
                  homepage: char.urls[0].url,
                  wiki: char.urls[1].url,
                  comics: char.comics.items,
            };
      };
      const { name, id, description } = chars
      console.log(chars);
      return (
            <div className='about'>
                  <div className='about__name'>{name}</div>
                  <div className='about__descr'>{description}</div>
                  <div className='about__id'>{id}</div>
            </div>
      );
}

export default About;
