import './forms.scss'
import Input from './inputs/Input';
import { Formik, Form } from 'formik';
import { initialValues } from './helper';


const Fofms = () => {
      return (
            <Formik
                  initialValues={initialValues}
                  validate={values => {
                        const errors = {};
                        if (!values.email) {
                              errors.email = 'Не мщжет быть пустым';
                        } else if (
                              !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                        ) {
                              errors.email = 'неправилдьный формат email адреса';
                        }
                        return errors;
                  }}
                  onSubmit={(values, { setSubmitting }) => {

                  }}
            >
                  <Form className='form'>
                        <Input id="email" name="email" lable="Email" placeholder="введите email" />
                  </Form>
            </Formik>

      );
}

export default Fofms;
