import './scssPages/about.scss'
import { useEffect, useState } from 'react';
import { useHttp } from '../../hooks/http.hook';
import Spinner from '../spinner/Spinner';


const About = () => {
      const { request } = useHttp()
      const [chars, setChar] = useState([])
      const [loading, setLoading] = useState(true)
      console.log(chars);

      useEffect(() => {
            res().then(setChar)


      }, [])
      const res = async () => {
            const results = await request("https://gateway.marvel.com:443/v1/public/characters?apikey=4ca4e0f7a1c0e3bdc1240a5027d68f5f")
            return results.data.results.map(_transformCharacter)
      };

      const _transformCharacter = (char) => {
            return {
                  id: char.id,
                  name: char.name,
                  description: char.description,
                  thumbnail: char.thumbnail.path + "." + char.thumbnail.extension,
                  homepage: char.urls[0].url,
                  wiki: char.urls[1].url,
                  comics: char.comics.items,
            };
      };
      //const { name, id, description } = chars

      return (
            <ul className='about'>
                  {loading ? <Spinner /> : <ComponentChar chars={chars} setLoading={setLoading} />}
            </ul>
      );
}



const ComponentChar = (props) => {
      const { chars } = props






      return (
            <div>
                  {
                        chars.map((item, i) => {

                              return (
                                    <li key={i} className='about__list'>
                                          <div className='about__list-name'>{item.name}</div>
                                          <div className='about__list-descr'>{item.description}</div>
                                          <div className='about__list-id'>{item.id}</div>
                                    </li>
                              )


                        })
                  }
            </div>

      )


}

export default About;
