import { useState } from 'react';
import './scssPages/formRegistr.scss'
import { useDispatch, useSelector } from 'react-redux';
import { onRegistrSubmitForm } from './slicePages/formRegisterSlice';

const FormRegisr = () => {
      const dispatch = useDispatch();
      const [formValueLogin, setFormValueLogin] = useState('');
      const [formValuePassword, setFormValuePassword] = useState('');
      //const { registerObj } = useSelector(state => state.formRegisterSlice.registerObj)

      //console.log(registerObj);
      const onSubmit = (e) => {
            e.preventDefault();
            const resObj = {
                  login: formValueLogin,
                  password: formValuePassword,
            }
            dispatch(onRegistrSubmitForm(resObj))
            console.log(resObj);
            setFormValueLogin('');
            setFormValuePassword('')
      }

      return (
            <div className='form_registr'>
                  <form onSubmit={onSubmit} className='forma__registration'>
                        <label htmlFor="">Логин: </label>
                        <input
                              className="form-control"
                              type="text"
                              required
                              onChange={(e) => setFormValueLogin(e.target.value)}
                              name="login"
                              value={formValueLogin}
                              id="login"
                              placeholder="Введите логин" />
                        <label htmlFor="" className='forma__registration'> Пароль:</label>
                        <input
                              type="password"
                              className="form-control"
                              required
                              onChange={(e) => setFormValuePassword(e.target.value)}
                              name="password"
                              value={formValuePassword}
                              id="password"
                              placeholder="Введите пароль"
                        />
                        <button
                              type="submit" className="btn btn-primary forma__registration">отправить</button>
                  </form>

            </div>
      );
}

export default FormRegisr;
