// Задача для этого компонента:
// Фильтры должны формироваться на основании загруженных данных
// Фильтры должны отображать только нужных героев при выборе
// Активный фильтр имеет класс active
// Изменять json-файл для удобства МОЖНО!
// Представьте, что вы попросили бэкенд-разработчика об этом
import { useHttp } from '../../hooks/http.hook';
import { useEffect } from 'react';
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { heroesFilter } from '../../actions';
import { useRef } from 'react';

const HeroesFilters = () => {

    const { request } = useHttp();
    const [stateButtons, setstateButtons] = useState([])
    const { heroes } = useSelector(state => state)
    const [category, setCategory] = useState("")
    const dispath = useDispatch()
    const activButton = useRef()
    console.log(activButton.current);

    useEffect(() => {
        request("http://localhost:3001/filters")
            .then(data => setstateButtons(data));

        renderFilter(heroes)
    }, [category]);


    const filterHeroesCategory =
        heroes.filter((item) => {
            return item.element === category
        })
    console.log(filterHeroesCategory);

    const renderFilter = () => {
        dispath(heroesFilter(filterHeroesCategory))
    }

    return (
        <div className="card shadow-lg mt-4">
            <div className="card-body">
                <p className="card-text">Отфильтруйте героев по элементам</p>
                <div className="btn-group">
                    {stateButtons.map((item) => {
                        return <button ref={activButton} onClick={(e) => setCategory(e.target.id)} id={item.key} key={item.key} className={item.className}>{item.name}</button>
                    })}
                </div>
            </div>
        </div>
    )
}

export default HeroesFilters;
