import React from 'react';
import ButtonLevel from './ButtonLevel';
import './Elevator.scss'
import { useRef } from 'react';


const Elevator = () => {

      const levelArr = [{
            children: 1,
            id: 1,
            className: 'level level_active'
      },
      {
            children: 2,
            id: 2,
            className: 'level'
      },
      {
            children: 3,
            id: 3,
            className: 'level'
      },
      {
            children: 4,
            id: 4,
            className: 'level'
      },
      {
            children: 5,
            id: 5,
            className: 'level'
      },
      {
            children: 6,
            id: 6,
            className: 'level'
      },
      ]
      // console.log(levelArr);

      const levelRef = useRef([])
      console.log(levelRef.current);



      return (
            <div className='elevator'>
                  {levelArr.map((item, i) =>
                        <button id={item.id}
                              className={item.className}
                              ref={(el) => (levelRef.current[i] = el)}
                        >
                              {item.children}
                        </button>
                  )}
            </div>
      );
}

export default Elevator;
