import { useEffect, useState } from 'react'
import './scssPages/hi.scss'
import { useSelector, useDispatch } from 'react-redux';
import Spinner from '../spinner/Spinner';
import { deleteShoppingCart } from './slicePages/aboutSlice';

const Hi = () => {

  const { v4: uuidv4 } = require('uuid');
  const shoppingCart = useSelector(state => state.aboutSlice.shoppingCart)
  const [stat, setStat] = useState([])
  const [check, setCheck] = useState({})
  const [count, setCount] = useState([])

  const dispatch = useDispatch()



  const counter = (arr) => {
    let lengthArr = arr.length
    //console.log(lengthArr);
    arr.forEach((el, i, array) => {

      return el.count[lengthArr]

    })
  }
  counter(count)


  useEffect(() => {
    resArray()
  }, [shoppingCart])

  const resArray = (() => {
    const filteredArray = Array.from(new Set(shoppingCart));
    setStat(filteredArray)
  })

  const deleteItem = (e) => {
    dispatch(deleteShoppingCart(shoppingCart.filter((item) => +item.id !== +e.target.id)))
  }


  const incr = (e) => {
    setCount([
      ...count, {
        id: e.target.id,
        count: count + 1,
  }
    ])
  }


  console.log(count);
  return (
    <div className='hi' >
      <ul className="hi__shoppingcart-ul">
        {stat < 1 ?
          <div className='hi__shoppingcart-empty'>
            Список пуст!! Добавь в карзину товар!
          </div> :
          stat.map((item, i) => {
          return (
            <li key={i} className='hi__shoppingcart-list'>
              <div className="hi__shoppingcart-wrapper">
                <div className='hi__shoppingcart-list-name'>{item.name}</div>
                <div className='hi__shoppingcart-list-descr'>{item.description}</div>
                <img src={item.thumbnail} className='hi__shoppingcart-list-img'></img>
                <div onClick={(e) => deleteItem(e)} id={item.id} className='hi__shoppingcart-delete'>❌
                </div>
              </div>
              <div className="hi__shoppingcart-select">
                <div className="hi__shoppingcart-selector">
                  <div onClick={(e) => incr(e)} id={item.id} className="hi__shoppingcart-selector-incr">+</div>
                  <div id={item.id} className="hi__shoppingcart-selector-summ">0</div>
                  <div id={item.id} className="hi__shoppingcart-selector-decr">-</div>
                </div>
                <input
                  type='checkbox'
                  className='hi__shoppingcart-checkbox'
                  id={uuidv4()}
                  onChange={() => console.log('g')}
                  //checked={stat}
                  //onClick={(e) => setStat(e)}
                  name="terms" />
              </div>

            </li>
          )
        })}



      </ul>
    </div>
  );
}



export default Hi;
