import * as Yup from 'yup'

const regx = {
      email: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/
      // i.test(values.email)
}

export const schemas = {
      custom: Yup.object().shape({
            email,
            password,

      })
}
const email = Yup.string()
      .matches(regx.email, 'Не правильный формат email адреса')
      .required('Не может быть пустым')
const password = Yup.number()
      .min(6, 'не менее 6 цифр')
      .max(12, 'не более 12 цифр')
      .matches(regx.password, 'Не правильный формат пароля')
      .required('Не может быть пустым')


export const initialValues = {
      email: "",
      password: '',
}
