import React from 'react';
import '../pages/scssPages/about.scss'
import Spinner from '../spinner/Spinner';
import { useDispatch, useSelector } from 'react-redux';
import { fetchChars, fetchChar, fetchShoppingCart, getShoppingCart, deleteChars } from '../pages/slicePages/aboutSlice';
import { useRef, useMemo, useState, useEffect } from 'react';

const AboutChars = () => {

  const dispatch = useDispatch();
  const refer = useRef([]);
  const { chars, stateDisable, cartChars } = useSelector(state => state.aboutSlice)
  const [charCart, setCharCart] = useState([])
  //const statusChar = useSelector(state => state.aboutSlice.statusChar)
  const urlAllChars = "https://gateway.marvel.com:443/v1/public/characters?limit=25&apikey=4ca4e0f7a1c0e3bdc1240a5027d68f5f"

  console.log(cartChars);
  const fetchData = useMemo(() => {
    dispatch(fetchChars(urlAllChars))
  }, [urlAllChars])


  const addArryChars = () => {

    if (cartChars.length === 0) {
      setCharCart(chars)
    } else { setCharCart(cartChars) }

  }


  const addCharsCart = (e) => {
    chars.forEach((el, i) => {
      if (el.id == e.target.id)
        dispatch(getShoppingCart(el))
    })

  }




  const deleteCharAddCart = (e) => {
    const delChars = chars.filter(item => item.id != e.target.id);
    dispatch(deleteChars(delChars))
    console.log(delChars);
  }



  // if (cartChars) {
  //   addCharCart = cartChars;
  // }





  return (
    <div>
      <button
        onClick={addArryChars}
        className='about__addchar'>
        добавить
      </button>
      {charCart.length === 0 ? <Spinner /> : <div>
        {
          charCart.map((item, i) => {
            return (
              <li ref={(el) => (refer.current[i] = el)}
                onClick={(e) => {
                  dispatch(fetchChar(+e.target.id));


                }}
                key={i}
                id={item.id}
                className='about__list' >
                <div className='about__list-name'>{item.name}</div>
                <div className='about__list-descr'>{item.description}</div>
                <div className='about__list-id'>{item.id}</div>
                <div className='about__list-i'>{i + 1}</div>
                <button
                  onClick={(e) => {
                    addCharsCart(e);
                    deleteCharAddCart(e);
                    // focuseItem(i, e);


                  }}

                  id={item.id}
                  className="about__list-button"
                >
                  добавить
                </button>
              </li>
            )
          })
        }
      </div>
      }
    </div>
  );
}

export default AboutChars;
