import { useState } from 'react';
import './scssPages/formRegistr.scss'
import { useDispatch, useSelector } from 'react-redux';
import { onRegistrSubmitForm, sendingfetchForm } from './slicePages/formRegisterSlice';

const FormRegisr = () => {

      const dispatch = useDispatch();
      const [formValueLogin, setFormValueLogin] = useState('');
      const [formValuePassword, setFormValuePassword] = useState('');
      const [checked, setChecked] = useState(false)
      const [loginRegister, setloginRegister] = useState()
      const { registerObj } = useSelector(state => state.formRegisterSlice)
      //const { stateCheckbox } = useSelector(state => state.formRegisterSlice)
      //const { v4: uuidv4 } = require('uuid');
      console.log(loginRegister);

      const toggleChecked = () => {
            setChecked(!checked)

      }

      const fetchData = async () => {
            const response = await fetch("http://localhost:3001/users");
            const data = await response.json();
            const ter = data.map((item) => {
                  return { [item.login]: item.password }
                  //console.log(item.login + item.password === formValueLogin + formValuePassword);
            })
            return ter.filter((el) => {
                  el === resObj
            })
      }


      fetchData()


      const onSubmit = (e) => {
            if (loginRegister === 'Registration') {
                  fetchData()
                  e.preventDefault()
                  const resObj = {
                        login: formValueLogin,
                        password: formValuePassword,
                  };
                  dispatch(sendingfetchForm(resObj))
                  setFormValueLogin('');
                  setFormValuePassword('');
                  setChecked(false);
            } else if (loginRegister === 'Authorization') {

            }




            //dispatch(onRegistrSubmitForm(resObj))



            // alert(`Статус отправки:
            //                         login: ${resObj.login},
            //                         password: ${resObj.password}`)

      }

      const optionChecked = loginRegister === 'Registration' ? "Согласие на регистрацию" : 'Согласие на авторизацию';
      const optionH1 = loginRegister === 'Registration' ? "Форма регистрации" : 'Форма авторизации';

      return (
            <div className='wrapper__register'>
                  <div className='form_registr'>
                        <h1>{optionH1}</h1>
                        <form onSubmit={onSubmit} className='forma__registration'>
                              <label htmlFor="login">Логин: </label>
                              <input
                                    className="form-control"
                                    type="text"
                                    required
                                    onChange={(e) => setFormValueLogin(e.target.value)}
                                    name="login"
                                    value={formValueLogin}
                                    id="login"
                                    placeholder="Введите логин" />
                              <label htmlFor="password" className='forma__registration'> Пароль:</label>
                              <input
                                    type="password"
                                    className="form-control"
                                    required
                                    onChange={(e) => setFormValuePassword(e.target.value)}
                                    name="password"
                                    value={formValuePassword}
                                    id="password"
                                    placeholder="Введите пароль"
                              />

                              <label htmlFor="element" className="forma__registration">Регистрация/Авторизация</label>
                              <select
                                    required
                                    onChange={(e) => setloginRegister(e.target.value)}
                                    className="form-select"
                                    id="element"
                                    value={loginRegister}
                                    name="element"
                              >
                                    <option></option>
                                    <option value="Registration">Регистрация</option>
                                    <option value="Authorization">Авторизация</option>

                              </select>

                              <label htmlFor='happy' className={checked ? 'custom-label custom-checkbox-active' : 'custom-label'}>
                                    <input id="happy"
                                          type="checkbox"
                                          onChange={toggleChecked}
                                          className='custom-checkbox'
                                          checked={checked}
                                    />{optionChecked}
                              </label>
                              <button
                                    disabled={!loginRegister || !checked || !formValuePassword || !formValueLogin} type="submit" className="btn btn-primary forma__registration">отправить</button>
                        </form>

                  </div>
            </div>

      );
}

export default FormRegisr;
