                  await fetch("https://gateway.marvel.com:443/v1/public/characters?apikey=4ca4e0f7a1c0e3bdc1240a5027d68f5f")
                  if (!response.ok) {
                        throw new Error(`Could not fetch ${url}, status: ${response.status}`);
                  }
                  const data = await response.json();
                  return data;

            } catch (error) {

                  throw (rejectWithValue(error.massage))
